#include "vcf/GraphicsKit/GraphicsKit.h"

using namespace VCF;


String VCF::Point::toString() const
{
	char txt[256];
	memset( txt, 0, 256 );
	sprintf( txt, "x: %.3f, y: %.3f", x_, y_ );

	return String( txt );
};


