void CommonFileDialog::addFilter(const String & description, const String & extension)
{
	String desc = description;
	String ext = extension;

	if ( getUseLocaleStrings() ) {
		desc = System::getCurrentThreadLocale()->translate( desc );
		ext = System::getCurrentThreadLocale()->translate( ext );
	}

	peer_->addFilter( desc, ext );
}

void CommonFileDialog::setDirectory(const String & directory)
{
	peer_->setDirectory( directory );
}

void CommonFileDialog::setFileName(const String & filename)
{
	peer_->setFileName( filename );
}

String CommonFileDialog::getFileName()
{
	String result;
	return peer_->getFileName();
}

String CommonFileDialog::getDirectory()
{

	return peer_->getDirectory();
}

String CommonFileDialog::getFileExtension()
{
	return peer_->getFileExtension();
}

void CommonFileDialog::setAllowsMultiSelect( const bool& allowsMultiSelect )
{
	peer_->setAllowsMultiSelect( allowsMultiSelect );
}

void CommonFileDialog::setTitle( const String& title )
{
	String text = title;
	
	if ( getUseLocaleStrings() ) {
		text = System::getCurrentThreadLocale()->translate( text );
	}

	peer_->setTitle( text );
}

String CommonFileDialog::getSelectedFilter()
{
	return peer_->getSelectedFilter();
}

void CommonFileDialog::setSelectedFilter( const String& selectedFilter )
{
	peer_->setSelectedFilter( selectedFilter );
}


